import React, { Component } from "react";
import "./App.css";
import CssBaseline from "@material-ui/core/CssBaseline";
import { ThemeProvider } from "@material-ui/styles";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import SearchIcon from "@material-ui/icons/Search";
import Profile from "./Profile";
import PantoneGallery from "./PantoneGallery";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = { query: "", mainPantone: null, pantoneSelection: null };
  }

  search() {
    const baseUrl = `https://vumavirtualcp.azurewebsites.net/api/ColorList?key=${this.state.query}`;

    console.log(baseUrl);

    fetch(baseUrl, {
      method: "GET"
    })
      .then(response => response.json())
      .then(json => {
        const pantone = json[0];
        this.setState({ mainPantone: pantone, pantoneSelection: json });
      });
  }

  render() {
    return (
      <div className="App">
        <CssBaseline />
        <ThemeProvider />
        <div className="app-title">Pantone Library</div>
        <div>
          <TextField
            placeholder="Search an artist..."
            fullWidth
            query={this.state.query}
            onChange={event => {
              this.setState({ query: event.target.value });
            }}
            onKeyPress={event => {
              if (event.key === "Enter") this.search();
            }}
          />
          &nbsp; &nbsp;
          <Button
            color="primary"
            variant="contained"
            fullWidth
            onClick={() => this.search()}
          >
            Search &nbsp;
            <SearchIcon />
          </Button>
        </div>
        {this.state.mainPantone !== null ? (
          <div>
            <Profile pantone={this.state.mainPantone} />
            <PantoneGallery pantones={this.state.pantoneSelection} />
          </div>
        ) : (
          <div></div>
        )}
      </div>
    );
  }
}

export default App;
